// Context2d
h = $.getContext("2d");

// Math
m = Math;

// Game state
n = 0;

// Frame counter
o = 0;

// Puzzle score
p = 50000;

// Current level score
q = 30000;

// Total score
r = 0;

// Stars position and size
s = []; for(i = 0; i < 300; i++) s[i] = [m.random() * 1200, m.random() * 650, m.random() + .5];

// Text
t = function(x, y, text, size, color, align){
    size = size || 50;
    color = color || "#fff";
    align = align || "center";
    h.textAlign = align;
    h.fillStyle = color;
    h.font = size + "px Impact, Charcoal";
    h.strokeStyle = "#000";
    h.strokeText(text, x, y);
    h.fillText(text, x, y);
}

// Stars scroll offset
u = 0;

// Earth rotation offset
v = 0;

// Time out
z = 0;

// Current level / puzzle
A = 0;
B = 0;

// Capitols - country map
D = { };

// Click coords
X = 0;
Y = 0;

/** Gather & shuffle the data **/

// Load the game's data in AJAX, as an arrayBuffer, from the file called "1"
a = new XMLHttpRequest;
a.open("GET", 1);
a.responseType = 'arraybuffer';
a.send();
a.onload = function(){
    
    b = new Uint8Array(a.response);
    
    c = 0;
    
    // Reconstitute all the data
    // For each category
    for(i in g){
        g[i] = g[i].split(1);
        
        // For each difficulty
        for(j in g[i]){
            g[i][j] = g[i][j].split(/(?=[A-Z])/);
            
            // For each name
            for(k in g[i][j]){
                
                // Make an array with the name and the coordinates
                // 2 coordinates for places, capitols, us capitols
                if(i < 3){
                    g[i][j][k] = [g[i][j][k], [b[c], b[c+1]]];
                    c += 2;
                }
                
                // List of coordinates separated by 254 for countries and us states
                else{
                    g[i][j][k] = [g[i][j][k], []];
                    while(b[c]!= 254 && c < b.length){
                        g[i][j][k][1].push(b[c]);
                        c++;
                    }
                    c++;
                }
            }
        }
    }
    
    for(i in g[1]){
        for(j in g[1][i]){
            if(g[3][i][j]){
                D[g[1][i][j][0]] = g[3][i][j][0];
            }
        }
    }
    
    for(i in g[2]){
        for(j in g[2][i]){
            if(g[4][i][j]){
                D[g[2][i][j][0]] = g[4][i][j][0];
            }
        }
    }
    
    // Hack for Algeria
    g[3][0][0][1] = [105,81,107,78,113,76,112,82,114,88,114,97,116,102,111,109,109,110,100,95,99,93,106,85,255,109,78];
    
    // Hack for Tallahassee
    g[2][0][6][1] = [207,218];
    
    // Hack for Bangui
    g[1][1][7][1] = [121,137];
    
    // Hack for raleigh
    g[2][0][17][1] = [226,182];
    
    // Hack to remove the last capitols
    delete g[1][2][74];
    delete g[1][2][75];
    delete g[1][2][76];
    delete g[1][2][77];
    
    // Launch game
    w();
}

/** Game loop **/
w = function(){
    
    // Reset canvas
    $.width ^= 0;
    
    // Welcome screen
    if(n == 0){
        
        // Background
        h.fillRect(0,0,1200,650);
        
        // Stars
        u--;
        u %= 1200;

        for(i = 0; i < 300; i++){
          h.fillStyle = "#fff";
          h.beginPath();
          h.arc(a = s[i][0] + u, b = s[i][1], c = s[i][2] + m.random() * .2, 0, 7);
          h.arc(1200 + a, b, c, 0, 7);
          h.fill();
        }

        // Earth

        // Background
        h.beginPath();
        gradient = h.createLinearGradient(300,0,600,0);
        gradient.addColorStop(0,"#75D1FF");
        gradient.addColorStop(1,"#3591bF");
        h.fillStyle = gradient;
        h.arc(470, 260, 140, 0, 7);
        h.fill();
        
        // Rotation
        v++;
        v %= 220;
        
        // Draw countries
        h.strokeStyle = "#83864F";
        h.fillStyle = "#95D866";
        
        // Loop on difficulties
        for(i = 4; i--;){
            
            // Loop on countries
            for(j = 0; j < g[3][i].length; j++){
                
                // Current country (avoid the gap fillers)
                if((i == 3 && j == 3)) a = [];
                else a = g[3][i][j][1];
                
                // Loop on coordinates
                for(k = 0; k < a.length; k += 2){
                    
                    // Start new island
                    if(a[k] == 255){
                        k++;
                        h.closePath();
                        h.fill();
                        h.stroke();
                        h.beginPath();
                    }
                    
                    // Hack
                    if(a[k + 1] ==  255){
                        k += 2;
                        h.closePath();
                        h.fill();
                        h.stroke();
                        h.beginPath();
                    }

                    // Current point
                    x = (a[k] - v + 100) / 110;
                    y = (a[k+1] - 120) / 150;
                    
                    while(x > 1) x -= 2;
                    if(x > -1 && x < -.5) x = -0.5;
                    if(x > .5 && x < 1) x = 0.5;
                    
                    // Fix canada & russia
                    if(!k && g[3][i][j][0] != "Russia" && g[3][i][j][0] != "Canada"){
                        if(x < 0) b = -.5;
                        else b = .5;
                    }
                    
                    if(g[3][i][j][0] == "Russia"){
                        b = .5;
                        if(v > 50) b = -.5;
                        if(v > 160) b = .5;
                    }
                    
                    if(g[3][i][j][0] == "Canada"){
                        b = .5;
                        if(v > 160) b = -.5;
                        if(v < 50) b = -.5;
                    }
                    
                    if((x <= -.5 || x >= .5)) x = b;
                    
                    x = m.sin(x * m.PI) * m.cos(y * m.PI / 2);
                    y = m.sin(y * m.PI / 2);
                    x = x * 140 + 470;
                    y = y * 140 + 260;
                    
                    // Start country
                    if(k == 0){
                        h.beginPath();
                        h.moveTo(x, y);
                    }
                    
                    // Continue country
                    h.lineTo(x, y);
                }
                
                h.closePath();
                h.fill();
                h.stroke();
            }
        }

        // Text
        t(180, 375, "GE", 300);
        t(890, 375, "Quiz", 300);
        t(990, 405, "JS13kGames 2015", 30);
        t(600, 570, "START", 80);
    }
    
    // Level presentation screen
    else if(n == 1){
        
        // Background
        h.fillRect(0, 0, 1200, 650);
        
        // Text
        t(600, 280, "Level " + (A + 1) + ":", 60);
        
        t(600,  360, [
        "Countries (easy)",
        "Capitols (easy)",
        "Famous places (easy)",
        "Countries (medium)",
        "Capitols (medium)",
        "U.S. states (medium)",
        "Famous places (medium)",
        "U.S. capitols (medium)",
        "World countries (hard)",
        "U.S. states (hard)",
        "Famous places (hard)",
        "U.S. capitols (hard)",
        "Capitols (hard)"
        ][A], 60);
        
        t(600, 450, "START", 30);
    }
    
    // Puzzle screen
    else if(n == 2 || n == 3){
        
        // Puzzles list
        C = [
            g[3][0],
            g[1][0],
            g[0][0],
            g[3][1],
            g[1][1],
            g[4][0],
            g[0][1],
            g[2][0],
            g[3][2],
            g[4][1],
            g[0][2],
            g[2][1],
            g[1][2]
        ][A];
            
        // Draw US map
        if(A == 5 || A == 7 || A == 9 || A == 11){
            f = 4
        }
        
        // Draw world map
        else{
            f = 3;
        }
            
        // Loop on difficulties
        for(i = g[f].length; i--;){
            
            // Loop on countries
            for(j = 0; j < g[f][i].length; j++){
                
                // Current country
                a = g[f][i][j][1];
                
                // Loop on coordinates
                for(k = 0; k < a.length; k += 2){
                    
                    // Start new island
                    if(a[k] == 255){
                        k++;
                        h.closePath();
                        h.fill();
                        h.stroke();
                        h.beginPath();
                    }
                    
                    // hack
                    if(a[k + 1] == 255){
                        k += 2;
                        h.closePath();
                        h.fill();
                        h.stroke();
                        h.beginPath();
                    }

                    // Current point
                    x = a[k] * (f == 4 ? 4.8 : 4.9);
                    y = a[k + 1] * (f == 4 ? 2.46 : 2.35) + (f == 4 ? 38 : 65);
                    if(B == 4 || B == 9){
                        x = 1200 - x;
                        y = 715 - y;
                    }
                    
                    // Start country
                    if(k == 0){
                        h.beginPath();
                        h.strokeStyle = B < 5 ? "#83864F" : "#000";
                        h.fillStyle = B < 5 ? "#95D866" : "#000";
                        "#95D866";
                        h.moveTo(x, y);
                    }
                    
                    // Continue country
                    h.lineTo(x, y);
                }
                
                h.closePath();
                h.fill();
                h.stroke();
            }
        }
        
        // Time out
        if(n == 2){
            if(o == 0){
                n = 3;
                o = 60;
                z = 1;
            }
        }
        
        // Puzzle feedback
        if(n == 3){
            
            // Draw target
            a = C[B][1];
            //_(C[B]);
            
            // Country / State
            if(A == 0 || A == 3 || A == 5 || A == 8 || A == 9){
                
                // Draw country/state in yellow
                h.fillStyle = "yellow";
                h.beginPath();

                // Loop on coordinates
                for(k = 0; k < a.length; k += 2){
                    
                    // Start new island
                    if(a[k] == 255){
                        k++;
                        h.closePath();
                        h.fill();
                        h.stroke();
                        if(o == 60){
                            if(h.isPointInPath(X, Y)){
                                p = 0;
                                c = [X, Y];
                            }
                        }
                        
                        h.beginPath();
                    }
                    
                    // hack
                    if(a[k + 1] == 255){
                        k += 2;
                        h.closePath();
                        h.fill();
                        h.stroke();
                        if(o == 60){
                            if(h.isPointInPath(X, Y)){
                                p = 0;
                                c = [X, Y];
                            }
                        }
                        h.beginPath();
                    }

                    // Current point
                    x = a[k] * (f == 4 ? 4.8 : 4.9);
                    y = a[k + 1] * (f == 4 ? 2.46 : 2.35) + (f == 4 ? 38 : 65);
                    if(B == 4 || B == 9){
                        x = 1200 - x;
                        y = 715 - y;
                    }
                    
                    // Test if it's the closest point to where we clicked
                    if(o == 60){
                        b = m.sqrt(m.pow(x - X, 2) + m.pow(y - Y, 2));
                        if(b < p){
                            p = b;
                            c = [x, y];
                        }
                    }
                    
                    // Start country
                    if(k == 0){
                        h.beginPath();
                        h.moveTo(x, y);
                    }
                    
                    // Continue country
                    h.lineTo(x, y);

                }
                
                h.closePath();
                h.fill();
                h.stroke();
                if(o == 60){
                    if(h.isPointInPath(X, Y)){
                        p = 0;
                        c = [X, Y];
                    }
                }
            }
            
            // Place / capitol
            else {
                x = a[0] * (f == 4 ? 4.8 : 4.9);
                y = a[1] * (f == 4 ? 2.46 : 2.35) + (f == 4 ? 38 : 65);
                if(B == 4 || B == 9){
                    x = 1200 - x;
                    y = 715 - y;
                }
                    
                h.fillStyle = "yellow";
                h.beginPath();
                h.arc(x, y, 10, 0, 7);
                h.fill();
                
                if(o == 60){
                    b = m.sqrt(m.pow(x - X, 2) + m.pow(y - Y, 2));
                    p = b;
                    c = [x, y];
                }
            }
            
            if(o < 60){
                
                // Tell the country of the current capitol
                if(A == 1 || A == 4 || A == 7 || A == 11 || A == 12){
                    t(600, 630, C[B][0].toUpperCase() + " IS THE CAPITOL OF " + D[C[B][0]].toUpperCase(), 40, "#fff", "center");
                }
            }

            // If not timeout
            if(!z){
                
                // Drop flags, trace red line
                if(p > 5){
                    h.fillStyle = "green";
                    h.strokeStyle = "green";
                    h.beginPath();
                    h.moveTo(c[0], c[1]);
                    h.lineTo(c[0]-1, c[1]);
                    h.lineTo(c[0]-1, c[1]-40);
                    h.lineTo(c[0], c[1]-40);
                    h.lineTo(c[0]+20, c[1]-30);
                    h.lineTo(c[0], c[1]-20);
                    h.stroke();
                    h.fill();
                    
                    h.strokeStyle = "red";
                    h.lineWidth = "2";
                    h.setLineDash([5, 5]);
                    h.beginPath();
                    h.moveTo(X, Y);
                    h.lineTo(c[0], c[1]);
                    h.stroke();
                }
                
                h.setLineDash([0,0]);
                h.fillStyle = "blue";
                h.strokeStyle = "blue";
                h.beginPath();
                h.moveTo(X,Y);
                h.lineTo(X-1, Y);
                h.lineTo(X-1, Y-40);
                h.lineTo(X, Y-40);
                h.lineTo(X+20, Y-30);
                h.lineTo(X, Y-20);
                h.stroke();
                h.fill();
            }
            
            // Update score
            if(o == 45){
                if(A == 5 || A == 7 || A == 9 || A == 11){
                    e = (p < 100 ? (~~(p/10)) * 100 : (~~(p/100)) * 1000);
                    q -= z ? 5000 : e;
                }
                else{
                    e =  (p < 100 ? (~~(p/4)) * 100 : (~~(p/40)) * 1000);
                    q -= z ? 10000 : e;
                }
            }
            
            // Text
            if(o < 45){
                if(z){
                    t(600, 350, "TIME OUT", 100, B < 5 ? "#000" : "#fff");
                    if(A == 5 || A == 7 || A == 9 || A == 11){
                        t(600, 400, "5,000km penalty", 50, B < 5 ? "#000" : "#fff");
                    }
                    else {
                        t(600, 400, "10,000km penalty", 50, B < 5 ? "#000" : "#fff");
                    }
                }
                else{
                    t(600, 350, p > 5 ? e + "km away" : "PERFECT", 100, B < 5 ? "#000" : "#fff")
                }
            }
            
            
            d = 0;
            
            // Game over
            if(q <= 0){
                q = 0;
                d = 1;
            }
            
            // Go to black screen before next puzzle
            if(o == 0) {
                n = 4;
                o = 15;
            }
        }
        
        // UI
        h.beginPath();
        h.fillStyle = "#000";
        h.rect(0, 0, 1200, 66);
        h.fill();
        h.closePath();
        h.beginPath();
        h.fillStyle = "#fff";
        if(n == 2){
            h.rect(0, 60, o * 4, 5);
            h.fill();
        }
        h.closePath();
        
        t(10, 45,
        [
            "Country",
            "Capitol",
            "Place",
            "Country",
            "Capitol",
            "U.S. state",
            "Place",
            "U.S. capitol",
            "Country",
            "U.S. state",
            "Place",
            "U.S. capitol",
            "Capitol"
        ][A]
        
        + ": " + 
        
        C[B][0].toUpperCase(), 40, 0, "left");
        
        t(1190, 45, q + "km remaining", 40, 0, "right");
    }
    
    // Black screen between puzzles
    if(n == 4){
        
        // Game over
        if(d){
            n = 5;
        }
        
        // Prepare next screen
        h.fillRect(0,0,1200,650);
        if(o == 0){
            
            // Reset click
            X = 0;
            Y = 0;
            
            // Next puzzle
            B++;
            n = 2;
            p = 50000;
            o = 300;
            z = 0;
            
            // Or next level
            if(B > 9){
                B = 0;
                A++;
                n = 1;
                r += q;
                q = 30000;
                if(A == 5 || A == 7 || A == 9 || A == 11){
                    q = 15000;
                }
                
                // Or you won
                if(A > 12){
                    n = 5;
                }
                
                // Faster music when we change levels
                M.t *= 1.075;
            }
            
            // Revert music on puzzles 4 and 9 (and revert it back to normal after)
            if(B == 4 || B == 9){
                M.r = true;
            }
            
            else {
                M.r = false;
            }
            
            
            // Black music on puzzles 5 to 9 (and revert it back to normal after)
            if(B >= 4 && B <= 9){
                M.s = 0.0;
                M.h = [60,10];
                M.b = 1.5;
                M.c = 0.6;
            }
            
            else {
                M.s = 0.1;
                M.h = [22,8,9,3,2,1];
                M.b = 1.5;
                M.c = 0.1;
            }
        }
    }
    
    // Game over
    if(n == 5){
        
        h.fillRect(0,0,1200,650);
        
        // Text
        t(600, 280, A == 13 ? "YOU WON!" : "GAME OVER!");
        
        t(600,  360, "Level " + (A + 1) + " ~ total score: " + r + "km", 40);
        
        t(600, 450, "REPLAY?", 30);
        
        Z.style.display = "block";
        
    }
    
    // Update frame counter
    if(o) o--;
    
    setTimeout(w, 33);
}


/** Handle Clicks **/
$.onclick = function(a){
    
    // Home screen => Level presentation screen
    if(n == 0){
        n = 1;
        
        // Shuffle data now
        b = function(){return .5 - m.random()}
        g[0][0].sort(b); // Places easy
        g[0][1].sort(b); // Places medium
        g[0][2].sort(b); // Places hard
        g[1][0].sort(b); // Capitols easy
        g[1][1].sort(b); // Capitols medium
        g[1][2].sort(b); // Capitols hard
        g[2][0].sort(b); // US capitols easy
        g[2][1].sort(b); // US capitols hard
        g[3][0].sort(b); // Countries easy
        g[3][1].sort(b); // Countries medium
        g[3][2].sort(b); // Countries hard
        g[4][0].sort(b); // US states easy
        g[4][1].sort(b); // US states hard
        //_(g);
    }
    
    // Level presentation screen => Puzzle screen
    else if(n == 1){
        n = 2;
        o = 300;
    }
    
    // Puzzle screen => Puzzle feedback
    else if(n == 2){
        X = a.pageX;
        Y = a.pageY;
        n = 3;
        o = 60;
    }
    
    // Game over => replay
    else if(n == 5){
        window.location = window.location;
    }
    
    // Other game states don't require clicking
}

// Music by Anders Kaare
// http://veralin.dk/2k.html
M=new (function(){
	if (!window.AudioContext) return;

	var M=this;

	var ctx=new AudioContext;
	var node=ctx.createScriptProcessor(4096,0,1);

	// "constants"
	var RATE=ctx.sampleRate/48e3;
	var FLOOR=Math.floor;
	var POW=Math.pow;
	var SIN=Math.sin;
	var RND=Math.random;
	var NSIN=function(x){return SIN(x)+RND()*0.6};
	var P2=Math.PI*2;
	var PRB=function(x){return RND()<x;};
	var OSC=function(fn){
		var x=0;
		return function(note) {
			x+=POW(2,note/12)*RATE;
			if(x>P2)x-=P2;
			return fn(x);
		};
	};

	var SEQ = [
		[0,1,4,5,7,10,12],
		[-2,0,1,4,5,8,1],
		[5,3,0,9,0],

		[0,4,7,1,-2],
		[1,3,5,7,8,10,12],
		[5,3,12,0,13]
	];

	var SPRB = [
		/* 0 */ 0.9,
		/* 1 */ 0.4,
		/* 2 */ 0.7,
		/* 3 */ 0.3,
		/* 4 */ 0.2,
		/* 5 */ 0.9,
		/* 6 */ 0.3
	];

	var BASS = [-84,-86,-79,-84,-83,-79];

	// oscillators
	var snare=OSC(NSIN);
	var kick=OSC(SIN);
	var pling=OSC(function(x) {
		var out = 0;
		for (var i = 0; i < M.h.length; i++) out+=SIN(x*(i+1))*M.h[i];
		return out;
	});
	var bass=OSC(function (x) {
		return (x/P2-0.5)+POW(SIN(x),5)*3;
	});

	var pos=1;
	var step=-1;
	var bigstep=0;
	var rstep=-1+(1<<24);
	var plinggate = true;
	var vibrato=0;
	
	node.onaudioprocess = function(e) {
		var data = e.outputBuffer.getChannelData(0);
		for (var i=0;i<data.length;i++) {
			pos+=M.t/RATE;
			vibrato+=0.001/RATE;
			if(pos>=1){
				pos-=1;
				step++;
				plinggate = PRB(SPRB[step%SPRB.length]);
				if (plinggate) {
					if (PRB(0.1)) rstep-=2;
					if (PRB(0.2)) rstep+=4;
					if (PRB(0.6)) rstep+=M.r?-1:1;
				}
			}
			bigstep=FLOOR(step/32);
			var ipos=1-pos;
			var pos2=(pos+(step&1))/2;
			var ipos2=1-pos2;
			var o=0;

			if (plinggate) {
				var seq=SEQ[bigstep%SEQ.length];
				o += pling(M.x-60+seq[rstep%seq.length]+SIN(vibrato*1.3)*M.v)*(M.r?pos2:ipos)*((step&1)?0.04:0.1)*M.p;
			}

			var bassi = step&3;
			var n = BASS[bigstep%BASS.length];
			if (bassi!=2) o += bass(M.x+n+(bassi==3?12:0)+SIN(vibrato)*M.c)*ipos*M.b*((step&1)?0.03:0.12);

			var drumx = M.r ? pos2 : ipos2;
			o += kick(-95+POW(drumx,5)*40)*M.k*POW(drumx,5);
			if (step&2) o += snare(-80+POW(drumx,15)*60)*M.s*POW(drumx,5);
			o += RND()*POW(ipos,10) * ((step&1)?0.03:0.1) * M.a;

			data[i]=o;
		}
	};
	node.connect(ctx.destination);

	// hi xem, this is the interface:
	M.stop = function() { node.disconnect(); } // XXX you can remove this to conserve space

	M.r = false; // set true for "reverse mode"
	M.k = 0.12; // kick volume
	M.a = 0.9; // hihat volume
	M.s = 0.1; // snare volume
	M.p = 0.06; // pling volume
	M.b = 1.5; // bass volume
	M.t=0.93e-4; // tempo
	M.h=[22,8,9,3,2,1]; // pling harmonies
	M.v=0.2; // pling vibrato intensity
	M.c=0.1; // bass vibrato intensity
	M.x=1; // master transpose
})
